    max-width: 320px;
    border-radius: 20px;
  }

  .storybook-image--gallery-thumb-bar {
    display: flex;
    justify-content: center;
    overflow-x: auto;
  }

  .storybook-image--gallery-thumb-bar img {
    display: block;
    width: 20%;
    height: 70px;
    float: left;
    cursor: pointer;
    border-radius: 20px;
    margin: 5px;
  }

  /* Slideshow styles */
  .storybook-image--slideshow{
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
  }

  .storybook-image--slideshow-image-container{
    position: relative;
  }

  .storybook-image--slideshow-image-wrapper{
    position: absolute;
  }
  .mySlides {
    display: none;
    height: 240px;
    width: fill-available;
    max-width: 320px;
    border-radius: 20px;
  }

  .mySlides.active {
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 240px;
    width: fill-available;
    max-width: 320px;
    border-radius: 20px;
  }

